mapper.select:
 select num,id,subject,content,img,notice,reg_date,nick,r
		from (select num,id,subject,content,img,notice,reg_date,nick,rownum r
		from (select num,id,subject,content,img,notice,reg_date, nick
		from support order by reg_date desc, notice asc)order by reg_date desc, notice asc)
	 	where r>=#{startRow} and r<=#{endRow}
